svg.script:
var angle_seconds = 0;
var angle_gear7_5 = 0;
var angle_minutes = 0;
var angle_gear180 = 0;
var angle_hours   = 0;

// Keep track of old time... so we don't update the display if not necessary.  
// This is a mechanical clock so it's OK to move the second hand once every second.
var old_time = 0;

// The gear groups
var seconds;
var gear7_5;
var minutes;
var gear180;
var hours;
var second_hand;
var minute_hand;
var hour_hand;

function Start(evt) {
  seconds     = evt.target.ownerDocument.getElementById("RotSeconds");
  gear7_5     = evt.target.ownerDocument.getElementById("RotGear7_5");
  minutes     = evt.target.ownerDocument.getElementById("RotMinutes");
  gear180     = evt.target.ownerDocument.getElementById("RotGear180");
  hours       = evt.target.ownerDocument.getElementById("RotHours"  );
  second_hand = evt.target.ownerDocument.getElementById("RotSecondHand"  );
  minute_hand = evt.target.ownerDocument.getElementById("RotMinuteHand"  );
  hour_hand   = evt.target.ownerDocument.getElementById("RotHourHand"    );

  Run();
}

function Run() {

  // Get computer clock time
  var date = new Date();
  var s = date.getSeconds();
  var m = date.getMinutes();
  var h = date.getHours();

  // Set start angles... this is a little tricky
  s += m * 60.0 + h * 3600.0;

  if( old_time != s ) {
    angle_seconds =  (s * 6.0         ) % 360.0;
    angle_gear7_5 = -(s * 6.0 /   7.5 ) % 360.0;
    angle_minutes =  (s * 6.0 /  60.0 ) % 360.0;
    angle_gear180 = -(s * 6.0 / 180.0 ) % 360.0;
    angle_hours   =  (s * 6.0 / 720.0 ) % 360.0;

    seconds.setAttribute("transform", "rotate(" +angle_seconds+ ")");
    gear7_5.setAttribute("transform", "rotate(" +angle_gear7_5+ ")");
    minutes.setAttribute("transform", "rotate(" +angle_minutes+ ")");
    gear180.setAttribute("transform", "rotate(" +angle_gear180+ ")");
    hours.setAttribute(  "transform", "rotate(" +angle_hours+   ")");

    second_hand.setAttribute("transform", "rotate(" +angle_seconds+ ")");
    minute_hand.setAttribute("transform", "rotate(" +angle_minutes+ ")");
    hour_hand.setAttribute(  "transform", "rotate(" +angle_hours+   ")");

  }

  old_time = s;

  // Repeat every 10th of a second.
  setTimeout("Run()", 100)
}

window.Run = Run
